-- Import 3 medical quizzes
DO $$
DECLARE
  quiz1_id UUID;
  quiz2_id UUID;
  quiz3_id UUID;
BEGIN
  -- Quiz 1: Neurorehabilitación 2025
  VALUES ('Neurorehabilitación 2025', 'Examen del módulo de Neurorehabilitación - Certificación SNA 2025', 30, false, true, 'published', 'system-import')
  RETURNING id INTO quiz1_id;


  -- Quiz 2: Epilepsia 2025
  VALUES ('Epilepsia 2025', 'Examen final del módulo de Epilepsia - Certificación SNA 2025', 35, false, true, 'published', 'system-import')
  RETURNING id INTO quiz2_id;


  -- Quiz 3: Enfermedades Cerebrovasculares 2024
  VALUES ('Enfermedades Cerebrovasculares 2024', 'Examen del módulo de Enfermedades Cerebrovasculares - Certificación SNA 2024', 35, false, true, 'published', 'system-import')
  RETURNING id INTO quiz3_id;


  RAISE NOTICE 'Successfully imported 3 quizzes with 75 questions total';
END $$;
